import React from "react"
import { ReticleProps } from "./ReticleProps"

const reticles = [
    React.lazy(() => import("./Reticle0")),
    React.lazy(() => import("./Reticle1")),
    React.lazy(() => import("./Reticle2")),
    React.lazy(() => import("./Reticle3")),
    React.lazy(() => import("./Reticle4")),
    React.lazy(() => import("./Reticle5")),
    React.lazy(() => import("./Reticle6")),
    React.lazy(() => import("./Reticle7"))
]

const Reticle: React.FC<ReticleProps & { variant?: number }> = ({ variant = 0, ...props }) => {
    return (
        <React.Suspense fallback={null}>
            {React.createElement(reticles[variant], props)}
        </React.Suspense>
    )
}

export default Reticle
