/**
 * A list constranit.
 * @template T The type of the value.
 */
export class ListConstraint {
    constructor(options) {
        this.options = options;
    }
    constrain(value) {
        const opts = this.options;
        if (opts.length === 0) {
            return value;
        }
        const matched = opts.filter((item) => {
            return item.value === value;
        }).length > 0;
        return matched ? value : opts[0].value;
    }
}
